<script setup>
import { useWindowSize } from '@vueuse/core';
import AgoraRTM from 'agora-rtm-sdk';
import { v4 as uuidv4 } from 'uuid';
import { ref, onMounted, nextTick, defineExpose } from 'vue';

const APP_ID = '452f99a0814b44d29d9a446ec20356fc';
const CHANNEL = 'wdj';
let client = AgoraRTM.createInstance(APP_ID);
let uid = uuidv4();
let text = ref('');
let messagesRef = ref(null);
let messages = ref([]);
let channel;
const route = useRoute();
const isLarge = ref(true);
const navIsShown = ref(true);
const input = ref('')
const { width } = useWindowSize();
let activeTab = ref(1);
const modal = ref(false);
const products = [
  {
    image: "no-photos.png",
  },
  {
    image: "girlla.png",
  },
  {
    image: "girlla.png",
  },
  {
    image: "girlla.png",
  },
]

defineExpose({ messagesRef });
const appendMessage = async (message) => {
  messages.value.push(message);
  await nextTick();
  messagesRef.value.scrollTop =
      messagesRef.value.scrollHeight;
};

watchEffect(() => {
  if (route.name.includes('chat-') && width.value <= 768) {
    navIsShown.value = false;
  } else {
    navIsShown.value = true;
  }
  if (width.value <= 768) {
    isLarge.value = false;
  } else {
    isLarge.value = true;
  }
});
onMounted(async () => {
  await client.login({ uid, token: null });
  channel = await client.createChannel(CHANNEL);
  await channel.join();
  channel.on('ChannelMessage', (message, peerId) => {
    appendMessage({
      text: message.text,
      uid: peerId,
    });
  });
  if (route.name.includes('chat-') && width.value <= 768) {
    navIsShown.value = false;
  } else {
    navIsShown.value = true;
  }
  if (width.value <= 768) {
    console.log('hello');
    isLarge.value = false;
  } else {
    isLarge.value = true;
  }
});

function sendMessage() {
  document.addEventListener('keypress', (e) => {
    if (e.key === 'Enter') {
      if (text.value === '') return;
      channel.sendMessage({ text: text.value, type: 'text' });
      appendMessage({
        text: text.value,
        uid,
      });
      text.value = '';
    }
  })
}
const open = () => {
  modal.value = true
}
const close = () => {
  modal.value = false
}
definePageMeta({
  layout: 'cabinet',
});
</script>

